INSERT INTO `sais_college` VALUES (10, '密歇根大学安娜堡分校', 'University of Michigan, Ann Arbor', 'US', '密歇根州', 20, 23.55, 'USNews美国', 27, '[\"商科\", \"法学\", \"医学\", \"工程学\", \"文理学\", \"建筑学\"]', 'mixiegendaxue.png', '密歇根大学安娜堡分校通常被称作密歇根大学。该校成立于1817年，是一所大型公立综合性全国大学，位于美国中西部小城安娜堡。密歇根大学安娜堡分校是美国著名公立大学系统——密歇根大学系统的旗舰学校。\r\n    密歇根大学号称美国的“学术重镇”以“研究”著称，校内拥有全美最高的研究预算、浓厚的学术气氛、优良的师资以及顶尖的商学院、法学院、医学院、工学院及文理学院，并在人类学、社会科学和STEM领域（科学、技术、工程、数学）以及专业学位的医学、法学、口腔学和社会工作领域开设综合性的研究生专业。学校在世界范围内享有盛名，被誉为“公立常春藤”，与加州伯克利大学、威斯康辛大学麦迪逊分校称为“公立大学典范”。', 43651, 28312, 15339, 15.00, 6.90, 0.00, '11月1日', '2月1日', '', 3.70, 1380, 100.00, 6.00, 100.00, '[\"高中成绩单\", \"申请费75美元\", \"在线申请表格\", \"学校报告\", \"SAT/ACT+写作成绩\", \"教师评估\", \"语言考试成绩\"]', '[\"推荐信\", \"申请费\", \"个人陈述\", \"个人简历\", \"成绩单\", \"在读证明/毕业证明\", \"学术目标陈述\"]', '[\"电气工程\", \"人类学\", \"建筑学\", \"生物化学\", \"应用物理\", \"政治学\", \"化学\", \"土木与环境工程\", \"比较文学\", \"经济学\", \"数学\", \"哲学\", \"物理\", \"心理学\", \"社会学\", \"公共政策\", \"航空航天工程\", \"生物医学工程\", \"化学工程\", \"传播学\", \"机械工程\", \"核工程\", \"物流管理\", \"会计\", \"工商管理\", \"语言学\", \"生物统计学\", \"流行病学\", \"社会工作\", \"药学\", \"法学\", \"城市设计\", \"护理\", \"医学化学\", \"材料科学与工程\", \"生物统计学\", \"化学生物学\", \"景观设计\", \"运动机能学\", \"工业工程\", \"城市与区域规划\", \"生物物理\", \"亚洲语言与文化\", \"统计学\", \"牙医\", \"环境卫生科学\", \"公共管理\", \"地球与环境科学\", \"创业管理\", \"比较法\", \"生物医学科学\", \"生态与进化生物学\", \"人类遗传学和基因组学\", \"荧屏艺术与文化\", \"自然资源与环境\", \"制药科学\", \"药物化学\", \"卫生信息学\", \"艺术与设计\", \"医学\", \"临床、社会和行政科学\", \"国际税法\", \"健康行为及教育\", \"卫生管理与政策\", \"信息学\", \"大气、海洋和空间科学\", \"计算机科学与工程\", \"系统与设计\", \"高分子科学与工程\", \"船舶与海洋工程\", \"音乐舞蹈与戏剧\", \"美国文化\", \"人类学与历史\", \"天文学与天文物理学\", \"古典艺术与考古学\", \"希腊与罗马历史\", \"俄国、东欧与欧亚研究\"]', '$49,350', '$11,534');
